import React, { Component } from 'react';
import PropTypes from 'prop-types';
import AlloyEditor from 'alloyeditor';
import EzEmbedImageButton from '../base/ez-embedimage';

export default class EzBtnImage extends EzEmbedImageButton {
    static get key() {
        return 'ezimage';
    }

    /**
     * Checks if the command is disabled in the current selection.
     *
     * @method isDisabled
     * @return {Boolean} True if the command is disabled, false otherwise.
     */
    isDisabled() {
        return !this.props.editor.get('nativeEditor').ezembed.canBeAdded();
    }

    /**
     * Executes the command generated by the ezembed plugin and set the
     * correct value based on the choosen image.
     *
     * @method addImage
     * @param {Array} items the result of the choice in the UDW
     */
    addImage(items) {
        this.execCommand();
        this.setContentInfo(items[0].ContentInfo.Content._id);

        const widget = this.getWidget()
            .setConfig('size', 'medium')
            .setImageType()
            .setWidgetContent('');
        widget.loadImagePreviewFromCurrentVersion(items[0].ContentInfo.Content.CurrentVersion._href);
        widget.setFocused(true);

        document.querySelector('#react-udw').innerHTML = '';
    }

    /**
     * Lifecycle. Renders the UI of the button.
     *
     * @method render
     * @return {Object} The content which should be rendered.
     */
    render() {
        const css = 'ae-button ez-btn-ae ez-btn-ae--image ' + this.getStateClasses(), disabled = this.isDisabled();

        return (
            <button className={css} disabled={disabled} onClick={this.chooseContent.bind(this)} tabIndex={this.props.tabIndex}>
                <svg className="ez-icon ez-btn-ae__icon">
                    <use xlinkHref="/bundles/ezplatformadminui/img/ez-icons.svg#image"></use>
                </svg>
                <p className="ez-btn-ae__label">Image</p>
            </button>
        );
    }
}

AlloyEditor.Buttons[EzBtnImage.key] = AlloyEditor.EzBtnImage = EzBtnImage;

EzBtnImage.defaultProps = {
    command: 'ezembed',
    modifiesSelection: true,
    udwTitle: 'Select an image to embed',
    udwContentDiscoveredMethod: 'addImage',
    udwIsSelectableMethod: 'isImage',
    udwLoadContent: true,
};
